mapper.select:


     and pr.factory_id = #{factoryId}
     and pr.asset_type = #{assetType}
     and pr.asset_id = #{assetId}
     and pr.risk_level = #{riskLevel}
     and pr.status = #{status}

        and pr.prediction_time >= #{beginPredictionTime}


        and pr.prediction_time <= #{endPredictionTime}


order by pr.prediction_time desc

mapper.update:
update prediction_result
factory_id = #{factoryId}, asset_type = #{assetType}, asset_id = #{assetId}, prediction_time = #{predictionTime}, risk_level = #{riskLevel}, risk_score = #{riskScore}, reason_summary = #{reasonSummary}, reason_detail_json = #{reasonDetailJson}, recommendation = #{recommendation}, status = #{status}, handled_by = #{handledBy}, handled_time = #{handledTime}, model_version = #{modelVersion}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate(),
where id = #{id}

mapper.select:
select count(*) from prediction_result 
where asset_type = #{assetType} 
  and asset_id = #{assetId} 
  and DATE(prediction_time) = #{dateStr}
